sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	console.log(newValue);
	var whatNeeded = g_form.getValue("u_what_needed");

	// Object to store value mappings
	var request_type_what_needed_map = {
		legal: function() {
			g_form.addOption("u_what_needed", "legal1", "Legal 1");
			g_form.addOption("u_what_needed", "legal2", "Legal 2");
			g_form.addOption("u_what_needed", "other", "Other");
		},
		facilities: function() {
			g_form.addOption("u_what_needed", "facilities1", "facilities 1");
			g_form.addOption("u_what_needed", "facilities", "facilities 2");
			g_form.addOption("u_what_needed", "other", "Other");
		},
		hr: function() {
			g_form.addOption("u_what_needed", "hr1", "HR 1");
			g_form.addOption("u_what_needed", "hr2", "HR 2");
			g_form.addOption("u_what_needed", "other", "Other");
		}
	};

	// u_request_type
	// u_what_needed

	g_form.clearOptions("u_what_needed");		// Remove old values
	request_type_what_needed_map[newValue](); 	// Invoke populate method from the mappings

	if(isLoading && !g_form.isNewRecord()) {
		g_form.setValue("u_what_needed", whatNeeded);
	}
}
