sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	// opened_by={pathParam}^active=true^u_request_typeINlegal,facilities,hr

	// Initial validation
	var requestedForUser = request.pathParams.user_name;
	var requestQuery = request.queryParams.ni_en_query;

	// Get user GlideRecord
	var userRecord = new GlideRecord('sys_user');
	userRecord.get('user_name', requestedForUser);

	// Obtain aggregate stats for Needit's for user
	var ga = new GlideAggregate('x_58872_needit_needit');
	ga.addAggregate('COUNT');
	ga.addQuery('u_requested_for', userRecord.getUniqueValue());
	ga.addEncodedQuery(requestQuery);
	ga.groupBy('u_request_type');
	ga.query();

	// Save record information for response
	var count = 0;
	var recs = [];
	var totalCount = 0;

	while (ga.next()) {
		count = ga.getAggregate('COUNT');
		recs.push({
			'requestType': ga.u_request_type.getDisplayValue(),
			'count': count
		});
		totalCount += parseInt(count);
	}

	// Construct response
	var body = {};
	body['needit_records_total'] = totalCount;
	body['needit_records_by_type'] = recs;
	body['user'] = {
		"username": userRecord.getValue('user_name'),
		"sysId": userRecord.getUniqueValue()
	};
	response.setBody(body);

})(request, response);
